mapper.select:
SELECT *


FROM bt_daily_return


WHERE task_id = #{taskId}

  AND trade_date = #{tradeDate}

SELECT *


FROM bt_daily_return


WHERE task_id = #{taskId}



         AND trade_date >= #{startTradeDate}     



         AND trade_date <= #{endTradeDate}

SELECT trade_date


FROM bt_daily_return


WHERE task_id = #{taskId}

SELECT DISTINCT t.id


FROM bt_daily_return dr
LEFT JOIN bt_task t ON dr.task_id = t.id


WHERE t.batch_no = #{batchNo}

AND dr.trade_date != '2020-02-03'


     AND t.max_drawdown_pct < -20     


     AND t.total_day < #{totalDays}     


         AND dr.daily_return <= #{dailyReturn}

mapper.delete:
DELETE FROM bt_daily_return
WHERE task_id IN
#{taskId}

DELETE
FROM bt_daily_return


WHERE task_id = #{taskId}



         AND trade_date >= #{startTradeDate}     



         AND trade_date <= #{endTradeDate}
