mapper.select:
SELECT *


FROM bt_position_record


WHERE task_id = #{taskId}

  AND trade_date = #{tradeDate}

  AND position_type = #{positionType}

SELECT *


FROM bt_position_record


WHERE task_id = #{taskId}



         AND trade_date >= #{startTradeDate}     



         AND trade_date <= #{endTradeDate}

mapper.delete:
DELETE FROM bt_position_record
WHERE task_id IN
#{taskId}

DELETE
FROM bt_position_record


WHERE task_id = #{taskId}



         AND trade_date >= #{startTradeDate}     



         AND trade_date <= #{endTradeDate}
